  @extend %buttons-general-rules;
  border-radius: 10px;
  margin: 0; padding: 0;
  box-sizing: border-box;
  width: 40px;
  line-height: 12px;
  vertical-align: middle;
  position: relative;
  &::before {
    content: url(../Task2/img/heart.svg);
    display: inline-block;
    width: 18px; height: 18px;
    line-height: 18px;
    position: absolute;
    left: 0;
  }
  &::after {
    content: "2";
    display: inline-block;
    margin: 0;
    font-size: 10px;
    color: $dark25;
    height: 18px;
    line-height: 18px;
    text-align: left;
    position: absolute;
    left: 22px
  }
}
.like-button-input {
  display: block;
  &:checked ~ .like-button-label{
    background-color: $purple;
  }
}
